import React, { useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import "./Weather.css"


const CurrentWeather = () => {
  const location = useLocation();
  const { weatherData } = location.state;
  let navigate = useNavigate();
  const routeChange = () =>{
    let path = '/';
    navigate(path);
  }
  const unitTypeSymbol = {
    'imperial': '°F',
    'metric': '°C',
    '': 'K',
  };

  useEffect(() => {
    console.log(weatherData);
  }, [weatherData]);

  if (!weatherData || !weatherData.main || !weatherData.weather || weatherData.weather.length === 0) {
    return <p>Invalid zipcode or city name</p>;
  }

  const Celsius = (kelvin) => {
    return Math.round(main.temp - 273.15);
  }

  const { main, weather } = weatherData;

  return (
    <div>
      <button className='button' onClick={routeChange}> Go back</button>
    <div className='data'>
    <div className="d-flex justify-content-center">
        <img src={"http://openweathermap.org/img/wn/${weatherData.weather[0].icon}@2x.png"} />
        </div>
      <h2>Current Weather for {location.pathname}</h2>
      <p>Temperature: {Celsius(main.temp) + "°C"}</p>
      <p>Weather: {weather[0].description}</p>
       <p>Feels like  {main.feels_like}</p>
                    
    </div>
    </div>
  );
};

export default CurrentWeather;
